import React from 'react';

import { Menu, Row, Col, Button, Carousel } from 'antd';
import { AppstoreOutlined, SettingOutlined, CheckOutlined } from '@ant-design/icons';
i
const Home = () => {
    const contentStyle = {
        height: '560px',

        background: '#fff',
    };
    return (
        <>
            <Row style={contentStyle}>
                <Col span={24} xs={{ order: 2 }} sm={{ order: 1 }} md={{ order: 4 }} lg={{ order: 3 }}>
                    <div className="content">
                        <h2 className="title" >Hãy quyên góp cho những hoạt động mà bạn quan tâm</h2>
                        <div className="commitment">
                            <div className="commitment__tick"><CheckOutlined />0% phí dịch vụ</div>
                            <div className="commitment__tick"><CheckOutlined />Thiết kế cho mục đích từ thiệt</div>
                            <div className="commitment__tick"><CheckOutlined />Được nhiều tổ chức từ thiện tin tưởng</div>
                        </div>
                        <div className="button">
                            <div className="button_wapper ">
                                <a type="button" className="ant-btn ant-btn-primary" href="/explore"><span className="btn-text">Explore Charities</span></a>
                            </div>
                            <div className="button_wapper ">
                                <a type="button" className="ant-btn ant-btn-default" href="/explore"><span className="btn-text">Explore Charities</span></a>
                            </div>

                        </div>
                        <p className="text-bold text-black">Chúng tôi đã quyên góp được</p>
                        <h2 class="text-bold"><span>$32,274,417.14</span></h2>
                        <div className="icon-micro-heart"><img width="50px" alt="Heart" src="../images/hands.svg" /></div>
                        <div class="mb-1x"><span class="text-medium"><div class="html-sanitizer">Hãy giúp chúng thôi một tay</div></span></div>
                        <div><a href="/about-us"><span class="text-bold text-uppercase">Tìm hiểu về chúng tôi</span></a></div>
                    </div>


                </Col>
            </Row>

        </>
    );
}

export default Home;
